/* 
    Login
    1 - need
    0 - only unlogged
    2 - don't nedd

*/

const Guard = (e: number = 2) => {
    if (e == 1 && !localStorage.getItem("token")){
        window.location.href = "/login"
        return
    }

    if (e == 0 && localStorage.getItem("token")){
        window.location.href = "/home"
        return
    }
}

export default Guard;
