import { useState } from "react";
import Nav from "../components/navBar";


function Home(){
    const [contador, constadorState] = useState(0);

    const plusplus = () => {
        constadorState(contador + 1);
    }

    return (
        <>
            <Nav></Nav>
            <main>
            <h1>
                {contador}
            </h1>
            <button className="pub" onClick={plusplus}>Aumenta</button>
            </main>
        </>
    );
    
}

export default Home;
